}

let todos = [];
function addTask() {
  let task = document.getElementById('input').value;
  todos.push(task);
  render();
}
function render() {
  document.getElementById('list').innerHTML = todos.map((t, i) => `<li>${t} <button onclick='del(${i})'>Delete</button></li>`).join('');
}
function del(i) {
  todos.splice(i, 1);
  render();
}
